mapper.select:
SELECT crew_name as name
FROM crew
WHERE crew_id = #{crewId} AND crew_pw = #{crewPw}

SELECT ask_title as askTitle, solo_content as soloContent, solo_answer as soloAnswer
FROM solo_ask
WHERE ask_num = #{askNum}
AND ask_pw = #{askPw}

mapper.update:
UPDATE faq
SET faq_content = #{faqContent}
WHERE faq_num = #{faqNum}

mapper.select:
SELECT ask_title as askTitle, solo_content as soloContent, solo_answer as soloAnswer
FROM solo_ask
WHERE ask_num = #{askNum}

mapper.update:
UPDATE solo_ask
SET solo_answer = #{soloAnswer}, crew_id = #{crewId}
WHERE ask_num = #{askNum}

mapper.insert:

    SELECT sprtp_num_seq.NEXTVAL FROM dual

insert into sprt_person(
    sprtp_num, sprt_title, sprt_link, sprt_summary, sprt_date, sprt_start, sprt_end, sprt_num
) values(
    #{sprtpNum}, #{title}, #{link}, #{summary}, SYSDATE, #{start}, #{end}, #{fkSprtNum}
)

mapper.update:
UPDATE sprt_person
SET sprt_title = #{title}, sprt_link = #{link}, sprt_summary = #{summary},
sprt_start = #{start}, sprt_end = #{end}
WHERE sprtp_num = #{sprtpNum}

UPDATE sprt_content
SET sprt_subtitle = #{subtitle}, sprt_content = #{content}
WHERE sprtpc_num = #{sprtpcNum}
