mapper.select:
SELECT crew_name as name
FROM crew
WHERE crew_id = #{crewId} AND crew_pw = #{crewPw}

SELECT ask_title as askTitle, solo_content as soloContent, solo_answer as soloAnswer
FROM solo_ask
WHERE ask_num = #{askNum}
AND ask_pw = #{askPw}

mapper.update:
UPDATE faq
SET faq_content = #{faqContent}
WHERE faq_num = #{faqNum}

mapper.select:
SELECT ask_title as askTitle, solo_content as soloContent, solo_answer as soloAnswer
FROM solo_ask
WHERE ask_num = #{askNum}

mapper.update:
UPDATE solo_ask
SET solo_answer = #{soloAnswer}, crew_id = #{crewId}
WHERE ask_num = #{askNum}
